mapper.select:
SELECT

FROM workspace
WHERE id = #{id}
AND tenant_id = #{tenantId}

mapper.update:
UPDATE workspace
SET name = #{name},
description = #{description},
update_at = NOW()
WHERE id = #{id}
AND tenant_id = #{tenantId}

mapper.delete:
DELETE
FROM workspace
WHERE id = #{id}
AND tenant_id = #{tenantId}
