mapper.select:
select

from workspace_folder
where tenant_id = #{tenantId}
and workspace_id = #{workspaceId}
and folder_type = #{folderType}
order by seq asc

select count(*)
from workspace_folder
where tenant_id = #{tenantId}
and workspace_id = #{workspaceId}
and parent_id = #{parentId}

select

from workspace_folder
where id = #{id}
and tenant_id = #{tenantId}
and workspace_id = #{workspaceId}

mapper.update:
update workspace_folder
set name = #{name},
description = #{description},
update_at = now()
where id = #{id}
and tenant_id = #{tenantId}
and workspace_id = #{workspaceId}

mapper.delete:
delete
from workspace_folder
where id = #{id}
and tenant_id = #{tenantId}
and workspace_id = #{workspaceId}

mapper.select:
select coalesce(max(seq), 0)
from workspace_folder
where tenant_id = #{tenantId}
and workspace_id = #{workspaceId}
and parent_id = #{parentId}

mapper.update:
update workspace_folder
set seq = seq + 1,
update_at = now()
where tenant_id = #{tenantId}
and workspace_id = #{workspaceId}
and parent_id = #{parentId}
and seq >= #{fromSeq}

update workspace_folder
set seq = seq - 1,
update_at = now()
where tenant_id = #{tenantId}
and workspace_id = #{workspaceId}
and parent_id = #{parentId}
and seq > #{fromSeq}

update workspace_folder
set parent_id = #{parentId},
seq = #{seq},
update_at = now()
where tenant_id = #{tenantId}
and workspace_id = #{workspaceId}
and id = #{id}
