mapper.select:
select

from workspace_tag
where tenant_id = #{tenantId}
and workspace_id = #{workspaceId}
and tag_type = #{tagType}

mapper.update:
update workspace_tag
set name = #{name},
    update_at = now()
where id = #{id}
  and tenant_id = #{tenantId}
  and workspace_id = #{workspaceId}
  and tag_type = #{tagType}

mapper.delete:
delete
from workspace_tag
where id = #{id}
  and tenant_id = #{tenantId}
  and workspace_id = #{workspaceId}
  and tag_type = #{tagType}
